package conditions;

public class multipleif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=65;
int b=95;
int c=85;

if(a>b && a>c) {
	System.out.println("a is greatest");
}
else if(b>a && b>c) {
	System.out.println("b is greatest");
}
else {
	System.out.println("c is greatest");
}
	}

}
